const pingColors = {
	good: 0xa2dfb6,
	avg: 0xf6c447,
	belowAvg: 0xf2a565,
	bad: 0xcd6674,
	failed: 0x101217,
};

const clamp = (a, min = 0, max = 1) => Math.min(max, Math.max(min, a));
const invlerp = (x, y, a) => clamp((a - x) / (y - x));

const lerpColor = (a, b, amount) => {
	const ar = a >> 16,
		ag = (a >> 8) & 0xff,
		ab = a & 0xff,
		br = b >> 16,
		bg = (b >> 8) & 0xff,
		bb = b & 0xff,
		rr = ar + amount * (br - ar),
		rg = ag + amount * (bg - ag),
		rb = ab + amount * (bb - ab);

	return (rr << 16) + (rg << 8) + (rb | 0);
};

const pad = (num, size) => {
	var s = '000000000' + num;
	return s.substr(s.length - size);
};

const getPingColor = (latency, active) =>
	pad(
		(() => {
			if (latency < 0 || !active) return pingColors.failed;

			if (latency < 20) return pingColors.good;

			if (latency < 60)
				return lerpColor(
					pingColors.good,
					pingColors.avg,
					invlerp(20, 60, latency)
				);

			if (latency < 120)
				return lerpColor(
					pingColors.avg,
					pingColors.belowAvg,
					invlerp(60, 120, latency)
				);

			if (latency < 200)
				return lerpColor(
					pingColors.belowAvg,
					pingColors.bad,
					invlerp(120, 200, latency)
				);

			if (latency < 500)
				return lerpColor(
					pingColors.bad,
					pingColors.failed,
					invlerp(200, 500, latency)
				);

			return pingColors.failed;
		})().toString(16),
		6
	);

