const App = {
	data() {
		return {
			servers: [],
			canvas: null,
		};
	},
	async mounted() {
		const ws = new WebSocket('ws://localhost:9911');

		const canvas = document.getElementById('worldmap');
		var ctx = canvas.getContext('2d');
		this.canvas = ctx;

		ws.onmessage = (e) => {
			try {
				const data = JSON.parse(e.data);
				this.servers = data;
				updateCanvas(this.canvas, this.servers);
			} catch (e) {
				console.error(e);
			}
		};
	},
};

Vue.createApp(App).mount('#App');

function updateCanvas(canvas, servers) {
	if (!canvas) return;
	canvas.clearRect(0, 0, 1920, 1080);
	console.log(servers.length);

	for (server of servers) {
		const { name, latency, active } = server;
		const { paths, pos, textPos } = worldParts[name];

		canvas.fillStyle = `#${getPingColor(latency, active)}aa`;
		canvas.translate(pos[0], pos[1]);

		canvas.scale(2.5, 2.5);
		paths.forEach((path) => {
			canvas.fill(new Path2D(path));
		});
		canvas.scale(1 / 2.5, 1 / 2.5);
		canvas.translate(-pos[0], -pos[1]);

		canvas.fillStyle = 'white';
		canvas.font = 'normal 64px Rubik';
		canvas.textAlign = 'center';

		if (active) canvas.fillText(`${latency}ms`, ...textPos);

		canvas.font = 'bold 24px Rubik';
		canvas.fillText(name, textPos[0], textPos[1] - 64);
		console.log(`Updated server ${name}!`);
	}
}
